r&#34;: &#34;all_ingredients_invalid&#34;,&#34;message&#34;: &#34;Tất cả nguyên liệu không hợp lệ. Vui lòng nhập lại.&#34;,&#34;invalid_ingredients&#34;: [&#34;abc&#34;, &#34;xyz&#34;, &#34;123&#34;],&#34;suggestions&#34;: [{ &#34;original&#34;: &#34;abc&#34;, &#34;similar&#34;: [&#34;cá&#34;, &#34;gà&#34;] },{ &#34;original&#34;: &#34;xyz&#34;, &#34;similar&#34;: [&#34;rau&#34;, &#34;củ&#34;] }]}Luồng Cooking History:
